<script>
  import {mapMutations, mapState} from 'vuex'
  import headTop from '../../components/header/head'
  import {getImgPath} from '../../components/common/mixin'
  import footGuide from '../../components/footer/footGuide'
  import {
    msiteAdress,
    discoverMember,
    discoverLike,
    discoverFood,
    discovernNearByOrders,
    discoverSuggest
  } from '../../service/getData'

  export default {
    data(){
      return {
        geohash: '', // city页面传递过来的地址geohash
        discover: {}, // discover模块数据
        foodLike: {}, // 美食热推 模块数据
        foodLikeParams: {
          offset: '0',
          request_id: 'd6361825-ca9f-4ecc-bece-b4bbc77ea7a9',
          tag_id: '-1',
          columns: '1'
        }, // 美食热推 模块参数
        discountFood: {}, // 天天特价 模块数据
        nearByOrders: {}, // 大家都在吃 模块数据
        suggest: {}, // 限时好礼 模块数据
      }
    },
    created(){
      this.initData();
    },
    mixins: [getImgPath],
    components: {
      headTop,
      footGuide,
    },
    computed: {
      ...mapState([
        'latitude', 'longitude'
      ])
    },
    props: [],
    methods: {
      ...mapMutations([
        'RECORD_ADDRESS'
      ]),
      //初始化获取数据
      async initData(){
        //获取从msite页面传递过来的参数
        this.geohash = this.$route.query.geohash;
        //防止刷新页面时，vuex状态丢失，经度纬度需要重新获取，并存入vuex
        if (!this.latitude) {
          //获取位置信息
          let res = await msiteAdress(this.geohash);
          this.latitude = res.latitude;
          this.longitude = res.longitude;
          // 记录当前经度纬度进入vuex
          this.RECORD_ADDRESS(res);
        }
        //获取discover数据
        this.discover = await discoverMember(this.latitude, this.longitude);
        //获取 美食热推
        this.foodLike = await discoverLike('0', this.latitude, this.longitude, this.foodLikeParams.request_id, this.foodLikeParams.tag_id, this.foodLikeParams.columns);
        //获取 天天特价
        this.discountFood = await discoverFood('0', this.latitude, this.longitude);
        //获取 大家都在吃
        this.nearByOrders = await discovernNearByOrders('0', this.latitude, this.longitude);
        //获取 限时好礼
        this.suggest = await discoverSuggest();
      },

    }
  }
</script>

